import osimport zipfilefrom zipfile import ZipFilefrom pathlib import Pathfrom shutil import movedownload_directory = Path ("/Users/jobe/Downloads")dir_name = "/Users/jobe/Downloads"extension = ".zip"for i in range(1, 3):    for p in download_directory.rglob("*"):        folder_name = p.stem.rsplit("_", 1)[-1]        p.rename(download_directory / p.name)    for item in os.listdir(dir_name): # loop through items in dir        if item.endswith(extension): # check for ".zip" extension            file_name = dir_name + "/" + item # get full path of files            zip_ref = zipfile.ZipFile(file_name) # create zipfile object            zip_ref.extractall(dir_name) # extract file to dir            zip_ref.close() # close file            os.remove(file_name) # delete zipped file    for p in download_directory.rglob('*'):        if p.is_dir() and len(list(p.iterdir())) == 0:            os.removedirs(p)            def get_dir(filename):        ext = filename.suffix[1:]        return dirs.get(ext, "Misc")            dirs = {        # Images        "jpeg": "Images",        "png": "Images",        "jpg": "Images",        "tiff": "Images",        "gif": "Images",            # Videos        "mp4": "Videos",        "mkv": "Videos",        "mov": "Videos",        "webm": "Videos",        "flv": "Videos",            # Music        "mp3": "Music",        "ogg": "Music",        "wav": "Music",        "flac": "Music",            # Program Files        "py": "Program Files",        "js": "Program Files",        "cpp": "Program Files",        "html": "Program Files",        "css": "Program Files",        "c": "Program Files",        "sh": "Program Files",            # Documents        "pdf": "Documents",        "doc": "Documents",        "docx": "Documents",        "txt": "Documents",        "ppt": "Documents",        "ods": "Documents",        "csv": "Documents"    }            # Directory Path    PATH = Path("/Users/jobe/Downloads")        for filename in PATH.iterdir():            path_to_file = filename.absolute()            if path_to_file.is_file():            destination = PATH / get_dir(filename)                if not destination.exists():                destination.mkdir()                move(str(path_to_file), str(destination))    i += 1# def clean(filetypes, path):#     for files in download_files:#         for f in filetypes:#             if files.endswith(f):#                 pof = os.path.join(os.getcwd(),files)#                 shutil.copy(pof,path)#                 os.remove(files)# clean([".doc", ".pdf", ".txt", ".docx"], "/Users/jobe/Documents")    # root = 'FOLDER'# folders = list(os.walk(root))[1:]# for folder in folders:#     # folder example: ('FOLDER/3', [], ['file'])#     if not folder[2]:#         os.rmdir(folder[0])# for root, dirs, files in os.walk(cwd, topdown = True):#     print(root)#     print(dirs)#     print(files)#     download_files = files#     print(download_files)# print(download_files)# print(os.getcwd())# import os# import shutil# directory = r"/Users/jobe/Downloads"# os.chdir(directory)# folder_list=os.listdir(directory)# for folders, dirs, files in os.walk(directory, topdown = False):#     for name in file# import os# def move_files_from_subfolders_into_root_folder(root_folder):#     for root, dirs, files in os.walk(root_folder, topdown=False):#         # 1. Move files to main folder (out of subfolders:)#         for name in files:#             os.system('move "'+os.path.join(root, name)+'" "'+root_folder+'\\'+os.path.join(name)+'"') #note \\ at the end #         # 2. Delete empty folders#         folders = list(os.walk(root_folder))[1:]#         for folder in folders:#             if not folder[2]:#                 os.rmdir(folder[0])#     # os.system('cls')# move_files_from_subfolders_into_root_folder(root_folder=r"/Users/jobe/Downloads")